// src/components/HybridQRScanner.jsx
import React, { useState, useEffect, useRef } from "react";
import { QrReader } from "react-qr-reader";

const HybridQRScanner = () => {
  const [scannedData, setScannedData] = useState(null);
  const [useCamera, setUseCamera] = useState(false); // toggle camera
  const inputRef = useRef(null);

  // Focus hardware scanner input on mount
  useEffect(() => {
    if (!useCamera) inputRef.current.focus();
  }, [useCamera]);

  // Handle hardware scanner input
  const handleInput = (e) => {
    const value = e.target.value.trim();
    if (!value) return;

    try {
      const parsed = JSON.parse(value);
      setScannedData(parsed);
    } catch (err) {
      console.error("Invalid QR data:", err);
      setScannedData({ error: "Invalid QR Code" });
    }

    e.target.value = ""; // clear input for next scan
  };

  // Handle camera scan
  const handleCameraScan = (result, error) => {
    if (!!result) {
      try {
        const parsed = JSON.parse(result?.text || result);
        setScannedData(parsed);
      } catch (err) {
        console.error("Invalid QR data:", err);
        setScannedData({ error: "Invalid QR Code" });
      }
    }
    if (error) {
      // optionally handle camera errors
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-background-light dark:bg-background-dark p-4">
      <h1 className="text-2xl font-bold text-black dark:text-white mb-4">
        Hybrid QR Scanner
      </h1>

      {/* Toggle camera */}
      <button
        className="mb-4 px-4 py-2 rounded bg-primary text-white hover:bg-primary/90"
        onClick={() => setUseCamera(!useCamera)}
      >
        {useCamera ? "Use Hardware Scanner" : "Use Camera"}
      </button>

      {/* Camera Scanner */}
      {useCamera && (
        <div className="w-full max-w-sm">
          <QrReader
            onResult={handleCameraScan}
            constraints={{ facingMode: "environment" }}
            containerStyle={{ width: "100%" }}
            videoStyle={{ width: "100%" }}
          />
        </div>
      )}

      {/* Hardware Scanner Input (hidden) */}
      {!useCamera && (
        <input
          ref={inputRef}
          onChange={handleInput}
          className="border border-slate-300 dark:border-slate-600 rounded p-2 mb-4 w-0 h-0 opacity-0"
          autoFocus
        />
      )}

      {/* Display scanned asset info */}
      {scannedData && (
        <div className="mt-4 p-4 bg-white dark:bg-slate-800 rounded shadow w-full max-w-md">
          <h2 className="font-semibold text-lg mb-2">Scanned Asset Info</h2>
          {scannedData.error ? (
            <p className="text-red-500">{scannedData.error}</p>
          ) : (
            <ul className="space-y-1 text-black dark:text-white text-sm">
              <li>
                <strong>Serial Number:</strong> {scannedData.serialNumber}
              </li>
              <li>
                <strong>Category:</strong> {scannedData.category}
              </li>
            </ul>
          )}
        </div>
      )}
    </div>
  );
};

export default HybridQRScanner;
