// src/components/AssetsDashboard.jsx
import React, { useEffect, useState } from "react";

const AssetsDashboard = () => {
  const [assets, setAssets] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchAssets = async () => {
      try {
        const res = await fetch("http://localhost:5000/api/asset/get/all");
        const data = await res.json();
        setAssets(data);
        setLoading(false);
      } catch (err) {
        console.error("Error fetching assets:", err);
        setLoading(false);
      }
    };
    fetchAssets();
  }, []);

  if (loading) {
    return (
      <div className="flex-1 flex items-center justify-center h-screen text-slate-700 dark:text-slate-300">
        Loading assets...
      </div>
    );
  }

  return (
    <main className="flex-1 px-4 py-8 sm:px-6 lg:px-8 bg-background-light dark:bg-background-dark min-h-screen">
      <div className="mx-auto max-w-7xl">
        {/* Header */}
        <div className="mb-6 flex flex-col sm:flex-row items-start sm:items-center justify-between gap-4">
          <h1 className="text-3xl font-bold text-black dark:text-white">
            Assets
          </h1>
          <button className="flex items-center gap-2 rounded-lg bg-primary px-4 py-2 text-sm font-semibold text-white shadow-sm hover:bg-primary/90">
            <span className="material-symbols-outlined text-base"> add </span>
            <span>Add New Asset</span>
          </button>
        </div>

        {/* Asset List */}
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {assets.length === 0 && (
            <div className="text-slate-500 dark:text-slate-400">
              No assets found.
            </div>
          )}
          {assets.map((asset) => (
            <div
              key={asset._id}
              className="bg-slate-100 dark:bg-slate-800 rounded-xl p-4 shadow-sm border border-slate-200 dark:border-slate-700"
            >
              <div className="flex justify-between items-center mb-2">
                <h2 className="text-lg font-semibold text-black dark:text-white">
                  {asset.assetName}
                </h2>
                {asset.generateQR && (
                  <span className="text-xs text-primary font-medium bg-primary/20 px-2 py-1 rounded">
                    QR
                  </span>
                )}
              </div>

              <p className="text-sm text-black/70 dark:text-white/70">
                <strong>Classification:</strong> {asset.classification || "-"}
              </p>
              <p className="text-sm text-black/70 dark:text-white/70">
                <strong>Description:</strong> {asset.description || "-"}
              </p>
              <p className="text-sm text-black/70 dark:text-white/70">
                <strong>Category:</strong> {asset.category || "-"}
              </p>
              <p className="text-sm text-black/70 dark:text-white/70">
                <strong>Serial Number:</strong> {asset.serialNumber || "-"}
              </p>
              <p className="text-sm text-black/70 dark:text-white/70">
                <strong>Purchase Date:</strong> {asset.purchaseDate || "-"}
              </p>
              <p className="text-sm text-black/70 dark:text-white/70">
                <strong>Issued Date:</strong> {asset.issuedDate || "-"}
              </p>
              <p className="text-sm text-black/70 dark:text-white/70">
                <strong>Issued To:</strong> {asset.issuedTo || "-"}
              </p>
            </div>
          ))}
        </div>
      </div>
    </main>
  );
};

export default AssetsDashboard;
